export declare const PRODUCT_SEARCH_QUERY = "\nquery searchProducts($query: String!, $first: Int, $productFilters: [ProductFilter!]) {\n    search(query: $query, first: $first, types: PRODUCT, productFilters: $productFilters) {\n        totalCount\n        nodes {\n            ... on Product {\n                id\n                title\n                vendor\n                handle\n                description\n                featuredImage {\n                    altText\n                    url\n                    height\n                    width\n                }\n                images(first: 5) {\n                    edges {\n                        node {\n                            id\n                            url\n                            altText\n                            height\n                            width\n                            originalSrc\n                            src\n                            transformedSrc\n                        }\n                    }\n                }\n            }\n        }\n        pageInfo {\n            hasNextPage\n            hasPreviousPage\n            startCursor\n            endCursor\n        }\n    }\n}\n";
